mapper.select:
SELECT ID, START, END, CRON, CONTROL_NAME, STATUS
FROM TASKCONTROL.CONTROL_SCHEDULES AS CS
WHERE CS.ID = #{controlId}

SELECT ID, START, END, CRON, CONTROL_NAME, STATUS
FROM TASKCONTROL.CONTROL_SCHEDULES AS CS
WHERE CS.STATUS = #{status,typeHandler=com.qopuir.taskcontrol.mybatis.typeHandlers.ControlScheduleStatusTypeHandler}

mapper.update:
UPDATE TASKCONTROL.CONTROL_SCHEDULES
SET STATUS = #{newStatus,typeHandler=com.qopuir.taskcontrol.mybatis.typeHandlers.ControlScheduleStatusTypeHandler}
WHERE ID = #{controlId}
